import React,{ useState} from 'react';

const App = () => {

  const [temperatureValue,setTemeratureValue]=useState(10);
  const [temperatureColor,setTemeratureColor]=useState('cold')


  const increaseTemp = () =>{
    const newTemp = temperatureValue +1;
    setTemeratureValue(newTemp);

    if(newTemp>=15){
      setTemeratureColor('hot');
    }
    setTemeratureValue()

  };

  return(
    <div className="app-container">
      <div className="temperature-display-container">
        <div className={'temperature-display ${temperatureColor}'}> {temperatureValue}Â°C</div>
      </div>
      <div className="button-container">
        <button onClick={() => increaseTemp()}>+</button>
        <button onClick={() => setTemeratureValue(temperatureValue - 1)}>-</button>
      </div>
    </div>
  )
}

export default App;
